import React, { useState } from 'react';
import './App.css';

function App() {
  const [screen, setScreen] = useState(null);
  const [logUser, setLogUser] = useState("");
  const [logPass, setLogPass] = useState("");
  const [newUser, setNewUser] = useState("");
  const [newPass, setNewPass] = useState("");


  useEffect(() => {
    socket.on("logIn", (username, pass) => {
      logUser(username, pass);
    });

    socket.on("user_left", (data) => {
      setUsers(data.users);
    });

    socket.on("start_game", () => {
      setShowLobby(false);
      setShowGame(true);
    });
  }, []);

  const handleNewUserClick = () => {
    setScreen('newUser');
  };

  const handleCurrentUserClick = () => {
    setScreen('currentUser');
  };

  const handleBackClick = () => {
    setScreen(null);
  };

  return (
    <div className="App">
      <header className="App-header">
        {screen === null && (
          <div>
            <button onClick={handleNewUserClick}>New User</button>
            <button onClick={handleCurrentUserClick}>Current User</button>
          </div>
        )}
        {screen === 'newUser' && (
          <div>
            <h2>Login In</h2>
            <button onClick={handleBackClick}>Back</button>

            <input
              type="text"
              placeholder="Username"
              onChange={(event) => {
                setLogUser(event.target.value);
              }}
            />

            <input
              type="text"
              placeholder="Password"
              onChange={(event) => {
                setLogPass(event.target.value);
              }}
            />

          </div>
        )}
        {screen === 'currentUser' && (
          <div>
            <h2>Gamers</h2>
            <button onClick={handleBackClick}>Back</button>

            <input
              type="text"
              placeholder="Username"
              onChange={(event) => {
                //setGame(event.target.value);
              }}
            />

            <input
              type="text"
              placeholder="Password"
              onChange={(event) => {
                //setGame(event.target.value);
              }}
            />

          </div>
        )}
      </header>
    </div>
  );
}

export default App;
