import React, { useState } from 'react';
import './App.css';
import io from 'socket.io-client';

const socket = io('http://localhost:3001'); // Specify server URL

function App() {
  const [screen, setScreen] = useState(null);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleNewUserClick = () => {
    setScreen('newUser');
  };

  const handleCurrentUserClick = () => {
    setScreen('currentUser');
  };

  const handleBackClick = () => {
    setScreen(null);
  };

  const handleLogin = () => {
    console.log('A user connected');
    // Emit username and password when login button is pressed
    socket.emit("logIn", { username, password });
  };

  return (
    <div className="App">
      <header className="App-header">
        {screen === null && (
          <div>
            <button onClick={handleNewUserClick}>New User</button>
            <button onClick={handleCurrentUserClick}>Current User</button>
          </div>
        )}
        {screen === 'newUser' && (
          <div>
            <h2>Login In</h2>
            <button onClick={handleBackClick}>Back</button>

            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(event) => setUsername(event.target.value)}
            />

            <input
              type="text"
              placeholder="Password"
              value={password}
              onChange={(event) => setPassword(event.target.value)}
            />

            <button onClick={handleLogin}>Login</button>
          </div>
        )}
        {screen === 'currentUser' && (
          <div>
            <h2>Gamers</h2>
            <button onClick={handleBackClick}>Back</button>

            <input
              type="text"
              placeholder="Username"
              onChange={(event) => {
                // Handle input change for new user (if needed)
              }}
            />

            <input
              type="text"
              placeholder="Password"
              onChange={(event) => {
                // Handle input change for new pass (if needed)
              }}
            />

            {/* Add logic for registering a new user (if needed) */}
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
